import * as React from 'react';
import { StatusBar } from 'expo-status-bar';
import {Text, View, StyleSheet, ImageBackground} from 'react-native';

export default function Home(){
 return(
    <View style={estilo.container}>
      <ImageBackground style={estilo.image} resizeMode="cover" source={require("../assets/home.gif")}></ImageBackground>
      <Text style={estilo.texto}>Bem-Vindo{'\n'}ao{'\n'}App de Cores</Text>
      <StatusBar style="auto" />
    </View>
 );   
}

const estilo = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#0e0121',
      alignItems: 'center',
      justifyContent: 'center',
    },
    imagem:{
        flex:1,
        width:500
    },
    texto: {
        color: 'white',
        fontSize: 50,
        textAlign: 'center',
        position: 'absolute'
    }
  });
